properties.query:
let parser=(disabled:bool=false) 
{
  ASimAuthenticationEventLogs  | where not(disabled)
    | extend
        User =  TargetUsername,
        Src = coalesce (SrcDvcId, SrcHostname, SrcIpAddr),
        IpAddr=SrcIpAddr,
        LogonTarget= coalesce (TargetAppName, TargetUrl, TargetHostname),
        Dst = coalesce (TargetHostname, TargetIpAddr, TargetAppId,TargetAppName), //TargerDvcId
        //Rule = coalesce(NetworkRuleName, tostring(NetworkRuleNumber)),
        EventStartTime = TimeGenerated,
        EventSchema = "Authentication"
};
parser  (disabled=disabled)
